mapper.select:
SELECT project_name FROM s3m_project WHERE customer_id IS NOT NULL
AND customer_id = #{customerId}
AND delete_flag != 1

SELECT customer_id, customer_name, customer_code FROM s3m_customer WHERE customer_id IS NOT NULL AND delete_flag = 0
AND customer_id = #{customerId}
AND delete_flag != 1

SELECT customer_id FROM s3m_user WHERE customer_id IS NOT NULL
AND customer_id = #{customerId}
AND delete_flag != 1

SELECT customer_id FROM s3m_project WHERE customer_id IS NOT NULL
AND customer_id = #{customerId}
AND delete_flag != 1

SELECT A.customer_id, A.customer_name, A.customer_code, A.description, A.update_date 
FROM s3m_customer  A
   WHERE A.customer_code = #{customerCode}
   AND A.delete_flag != 1

        AND A.customer_id != #{customerId}


SELECT A.customer_id, A.customer_name, A.customer_code, A.description, A.update_date 
FROM s3m_customer  A
   where A.customer_id = #{customerId}

