SELECT A.id AS objectId, A.name AS objectTypeName, B.img, D.project_name AS projectName, B.name AS objectName, E.customer_name AS customerName FROM s3m_object A JOIN s3m_object_type_mst B ON B.id = A.object_type_id JOIN s3m_device C ON A.id = C.object_id JOIN s3m_project D ON C.project_id = D.project_id JOIN s3m_customer E ON D.customer_id = E.customer_id WHERE A.id = #{objectTypeId}
AND D.project_id = #{projectId}
AND C.system_type_id = #{systemTypeId}
GROUP BY objectId, objectTypeName, objectName

